/**
 * A Key => Value store for all of the dependencies
 * in a web application.
 * @class DIC
 * @memberOf module:edu/dat/Web
 * @constructor
 */
var DIC;

DIC = (function() {

	function DIC()
	{
		this.definitions = {};
	};

	/**
	 * Set a value for a name.
	 * @instance
	 * @memberOf module:edu/dat/Web.DIC
	 * @method set
	 * @param {String} name       The name of the object to add.
	 * @param {Object} definition The object.
	 */
	DIC.prototype.set = function(name, definition)
	{
		this.definitions[name] = definition;
	};

	/**
	 * Get a value for a name
	 * @instance
	 * @memberOf module:edu/dat/Web.DIC
	 * @method get
	 * @param  {String} name       The name of the object.
	 * @return {Object}            The object.
	 */
	DIC.prototype.get = function(name)
	{
		return this.definitions[name];
	};

	return DIC;

})();

module.exports = DIC;